import BackGroundImage from './../FIFA-logo.jpg';
import Papa from 'papaparse';
import fs from 'fs';
import { thresholdScott } from 'd3';


class TeamsPage extends React.Component {

    constructor(props){
        super(props);
        this.state = {LeagueID: this.props.leagueID,
                      TeamID: [],
                      NoDuplicate: true}
    }

    componentDidMount(){ 
        this.someFunction();
    }

    // const data_url = "acdata.csv";
    async getData() {
        // create a function that returns a Promise that resolves when papa parse complete is called
        const papaParsePromise = blob => new Promise(resolve => Papa.parse(blob, { complete: resolve }));
        const response = await fetch('./matches.csv');
        const blob = await response.blob();
        const data = await papaParsePromise(blob);
        return data;
    };



    addTeamOption = async () => {
        const data_url = "./matches.csv";
        async function getData() {
            // create a function that returns a Promise that resolves when papa parse complete is called
            const papaParsePromise = blob => new Promise(resolve => Papa.parse(blob, { complete: resolve }));
            const response = await fetch(data_url);
            const blob = await response.blob();
            const data = await papaParsePromise(blob);
            return data;
        };
        // getData()
        // .then(parse_data => {
