import React from 'react';
import BackGroundImage from './../FIFA-logo.jpg';
import Papa from 'papaparse';
import * as d3 from "d3";


class ResultDisplayPage extends React.Component {

    constructor(){
        super();
        this.myRef = React.createRef(); 
        this.state = {
            gameResultHome: [],
            gameResultAway: [],
            gameResult: []
        }
    }
    componentDidMount(){ 
        console.log(this.myRef);
        this.readDataBase();
    }

    // const data_url = "acdata.csv";
    async getData(URL) {
        // create a function that returns a Promise that resolves when papa parse complete is called
        const papaParsePromise = blob => new Promise(resolve => Papa.parse(blob, { complete: resolve }));
        const response = await fetch(URL);
        const blob = await response.blob();
        const data = await papaParsePromise(blob);
        return data;
    };

    async readDataBase() {
        const season = this.props.season;
        const parsed_data = await this.getData('./unitedMatches.csv');
        const matchesData = parsed_data.data;
        for(var i = 0; i < matchesData.length; ++i){
            const matchesDataSplit = matchesData[i];
            if (matchesDataSplit[2] === season){
                if (matchesDataSplit[6] === 10260){ // home team
                    var scoreDiff = matchesDataSplit[8] - matchesDataSplit[9];
                    await this.setState(prevState => ({ gameResult: prevState.gameResult.concat(scoreDiff)}));
                }
                else{
                    var scoreDiff = matchesDataSplit[9] - matchesDataSplit[8];
                    await this.setState(prevState => ({ gameResult: prevState.gameResult.concat(scoreDiff)}));
                }
            }
        }
        // console.log(matchesData);
        console.log(this.state.gameResult);
        this.displayD3();
    }
    displayD3(){
        let size = 500;
        let svg = d3.select(this.myRef.current)
                    .append('svg')
                    .text('Hello from D3')
                    .attr('width', size)
                    .attr('height', size);
                    let rect_width = 95;
        svg.selectAll('rect')
            .data(this.state.gameResult)
            .enter()
            .append('rect')
            .attr('x', (d, i) => 5 + i*(rect_width + 5))
            .attr('y', d => (size - d))
            .attr('width', rect_width)
            .attr('height', d => d)
            .attr('fill', 'teal');
    }

    render() {
        return (
        <div className="padded-page FIFA_wrap">
            <center>
            <div id = "SelectLeague" className = "dropdown">
                <h2>Result</h2>
                <img src={BackGroundImage} alt="bg"></img>
                <p></p>
                <p></p>
                {/* <p style={{fontStyle: "italic"}}>Version CptS 575 Final Project</p> */}
            </div>
            <div ref={this.myRef}>
            </div>
            <p style={{fontStyle: "italic"}}>&copy;Version CptS 575 Final Project</p>
            </center>
        </div>
        );
    }   
}

export default ResultDisplayPage;
