import React from 'react';
import BackGroundImage from './../FIFA-logo.jpg';
import Papa from 'papaparse';
import { thresholdScott } from 'd3';


class TeamsPage extends React.Component {

    constructor(props){
        super(props);
        this.state = {LeagueID: this.props.leagueID,
                      TeamID: [],
                      NoDuplicate: true}
    }

    componentDidMount(){ 
        this.addTeamOption(this.state.LeagueID);
    }

    addTeamOption = async (leagueID) =>{
        const data = Papa.parse(await fetchCsv());
        const matchesData = data.data;
        console.log(matchesData[9]);

        async function fetchCsv() {
            const response = await fetch('./matches.csv');
            const reader = response.body.getReader();
            const result = await reader.read();
            const decoder = new TextDecoder('utf-8');
            const csv = await decoder.decode(result.value);
            // console.log('csv', csv);
            return csv;
        }

        for(var i = 0; i < 5; ++i){
          const matchesDataSplit = matchesData[i]
          console.log(matchesDataSplit[1], matchesDataSplit[7], leagueID);
        //   if(matchesDataSplit[1] === leagueID){
        //       if(this.checkDuplicate(matchesDataSplit[7]) === false){
        //         continue;
        //       }
        //       else{
        //         await this.setState({TeamID: this.state.TeamID.concat(matchesDataSplit[7])});
        //       }
        //   }
        }
      }
    async checkDuplicate (c){
        for(var check = 0; check < this.state.TeamID.length; ++check){
            console.log("Team array " + this.state.TeamID[check]);
            console.log("Check value " + c);
            if(this.state.TeamID[check] === c){
                await this.setState({NoDuplicate: false});
            }
        }
    }

    selectTeam = (event) =>{
        this.props.selectTeam(event.target.value);
    }
    setLeagueID = async(id) =>{
        await this.setState({LeagueID: id});
        console.log(this.state.LeagueID);
    }

    render() {
        this.setLeagueID(this.props.LeagueID);
        return (
        <div className="padded-page FIFA_wrap">
            <center>
            <div id = "SelectTeam" className = "dropdown">
                <h2>Welcome to FIFA Analysis</h2>
                <p>League: {this.props.League}</p>
                <p>Please Select a Team.</p>
                <select id="myDropdown" className="dropdown-content" onChange={this.selectTeam}>
                    <option value="0">Select Team</option>
                </select>
                <img src={BackGroundImage} alt="bg"></img>
                <p></p>
                <p></p>
                <p style={{fontStyle: "italic"}}>Version CptS 575 Final Project</p>
            </div>
            </center>
        </div>
        );
    }   
}

export default TeamsPage;
